import React from 'react';
import logoImage from '../images/logo-header.svg';
import { Link, useLocation } from 'react-router-dom';

function Header({ loggedIn, email }) {
  const { pathname } = useLocation();
  const linkText = `${pathname === '/sign-in' ? 'Регистрация' : 'Войти'}`;
  const linkPath = `${pathname === '/sign-in' ? '/sign-up' : '/sign-in'}`;

  return (
    <header className="header page__header section">
      <img className="logo header__logo" src={logoImage} alt="Логотип" />
      {loggedIn ? (
        <div className='auth-info'>
          <span>{email}</span>
          <button className='auth-info__signout' onClick={signOut}>Выйти</button>
        </div>
        )
        : (<Link to={linkPath} className="link header__link">{linkText}</Link>)
      }
    </header>
  );
}

export default Header;



