import React from 'react';
import PopupWithForm from './PopupWithForm';

function InfoTooltip({ isOpen, onClose, message }) {
  return (
    <PopupWithForm 
      isOpen={isOpen}
      onClose={onClose}
    >
      <img src={message.icon} alt='Иконка авторизации' className='form__icon' />
      <p className='form__text'>{message.text}</p>
    </PopupWithForm>
  );
}

export default InfoTooltip;
