import React from 'react';

function Login() {
  return (
  <div className="auth"> 
    <div className="auth__container">
      <h2 className="auth__title">Вход</h2>
      <form className="auth__form" action="#" noValidate>
      <label>
          <input type="text" value="" className="auth__item auth__item_type_email" name="email" placeholder="Email" required minLength="2" maxLength="40" />
          <span id="email-error" className="modal__item-error"></span>
        </label>
        <label>
          <input type="password" value="" className="auth__item auth__item_type_password" name="password" placeholder="Пароль" required minLength="2" maxLength="200" />
          <span id="password-error" className="modal__item-error auth__item-error"></span>
        </label>
        <button type="submit" className="modal__button modal__button_action auth__button">Войти</button>
      </form>
    </div>
  </div> 
  );
}

export default Login;
