import React from 'react';
import Header from './Header.js';
import Footer from './Footer.js';
import Main from './Main.js';
import ImagePopup from './ImagePopup.js';
import PopupWithForm from './PopupWithForm.js';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js'
import api from '../utils/api.js';
import CurrentUserContext from '../contexts/CurrentUserContext.js';

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isImagePopupOpen, setImagePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [currentUser, setCurrentUser] = React.useState({name: 'Жак-Ив Кусто', about: 'Исследователь океана'});

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }

  function closeAllPopups() {
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setEditAvatarPopupOpen(false);
    setImagePopupOpen(false);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
    setImagePopupOpen(true);
  }

  function handleUpdateUser(info) {
    api.setUserInfo(info)
      .then((info) => {
        setCurrentUser(info);
        closeAllPopups()
      })
      .catch((err) => console.log(err));
  }

function handleUpdateAvatar(link) {
  api.setUserAvatar(link)
      .then((info) => {
        setCurrentUser(info);
        closeAllPopups()
      })
      .catch((err) => console.log(err));
}

  React.useEffect(() => {
    api.getUserInfo()
      .then((info) => {
        setCurrentUser(info);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">
          <div className="page__container">
            <Header />
            <Main 
            onEditAvatar={() => {
              handleEditAvatarClick();
            }}
            onEditProfile={() => {
              handleEditProfileClick();
            }}
            onAddPlace={() => {
              handleAddPlaceClick();
            }}
            onCardClick={(card) => {
              handleCardClick(card);
            }}
            />
            <Footer />
          </div>
          <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
          <PopupWithForm 
            name={'add-card'} 
            title={'Новое место'} 
            children={
              <>
                <label>
                  <input type="text" className="modal__item modal__item_type_place" name="name" defaultValue="" placeholder="Название" required minLength="1" maxLength="30" />
                  <span id="name-error" className="modal__item-error"></span>
                </label>
                <label>
                  <input type="url" className="modal__item modal__item_type_link-place" name="link" defaultValue="" placeholder="Ссылка на картинку" required />
                  <span id="link-error" className="modal__item-error"></span>
                </label>
              </>
            } 
            text={'Создать'}
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
          />
          <PopupWithForm 
            name={'confirm'} 
            title={'Вы уверены?'} 
            text={'Да'} 
          />
          <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} /> 
          <ImagePopup 
            card={selectedCard}
            isOpen={isImagePopupOpen}
            onClose={closeAllPopups}
          />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
