import React from 'react';
import PopupWithForm from './PopupWithForm.js';
import CurrentUserContext from '../contexts/CurrentUserContext.js';

function EditProfilePopup({ isOpen, onClose }) {
  const [name, setName] = React.useState('');
  const [description, setDescription] = React.useState('');
  const currentUser = React.useContext(CurrentUserContext);

  // function handleChangeName() {
  //   setName();
  // }

  // function handleChangeDescription() {
  //   setDescription();
  // }

  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser]); 

  return (
          <PopupWithForm 
            name={'edit-profile'} 
            title={'Редактировать профиль'} 
            children={
              <>
                <label>
                  <input type="text" value={name} onChange={setName} className="modal__item modal__item_type_name" name="name" placeholder="ФИО" required minLength="2" maxLength="40" />
                  <span id="name-error" className="modal__item-error"></span>
                </label>
                <label>
                  <input type="text" value={description} onChange={setDescription} className="modal__item modal__item_type_profession" name="about" placeholder="Профессия" required minLength="2" maxLength="200" />
                  <span id="about-error" className="modal__item-error"></span>
                </label>
              </>
            } 
            text={'Сохранить'}
            isOpen={isOpen}
            onClose={onClose}
          />
  );
}

export default EditProfilePopup;
