import React from 'react';
import api from '../utils/api.js';

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
  const [userName, setUserName] = React.useState();
  console.log(userName);
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();

  React.useEffect(() => {
    api.getUserInfo()
    .then((info) => {

      document.querySelector('.profile__title').textContent = info.name;
      document.querySelector('.profile__subtitle').textContent = info.about;
      document.querySelector('.profile__image').src = info.avatar;
    });
  });


  return (
    <main className="content page__content">
      <section className="profile section content__section">
        <div className="profile__info">
          <button className="profile__image-button" type="button" onClick={onEditAvatar}>
            <div className="profile__icon"></div>
            <img src={userAvatar} alt="фото" className="profile__image" />
          </button>
          <div className="profile__edit">
            <div className="profile__text">
              <h1 className="profile__title">{userName}</h1>
              <p className="profile__subtitle">{userDescription}</p>
            </div>
            <button className="profile__edit-button" type="button" onClick={onEditProfile}></button>
          </div>
        </div>
        <button className="profile__add-button" type="button" onClick={onAddPlace}></button>
      </section>
      <section className="elements section content__section">
        <ul className="elements__list"></ul>
      </section>
    </main>
  );
}

export default Main;
