import React from 'react';

function PopupWithForm({ name, title, children }) {
  return (
    <div className={`modal modal_type_${name}`}>
      <div className="modal__container">
        <button className="modal__close-button" type="button"></button>
        <h2 className="modal__title">{title}</h2>
        <form className="modal__form" action="#" name={`modal_${name}_form`} noValidate>
        {children}
        <button type="submit" className="modal__button modal__button_action">Сохранить</button>
        </form>
      </div>
    </div>
  );
}

export default PopupWithForm;



