import React from 'react';

function Form({ isModal, name, title, children, text, message, onSubmit }) {
  console.log(message)
  return (
    <>
      {
        isModal
        ? (
          <>
            <h2 className="modal__title">{title}</h2>
            <form className="modal__form" onSubmit={onSubmit} action="#" name={`modal_${name}_form`} noValidate>
              {children}
              <button type="submit" className="modal__button modal__button_action">{text}</button>
            </form>
          </>
        ) :
        (
          <>
            <img src={message.icon} alt='Иконка авторизации' className='form__icon' />
            <p className='modal__tooltip-text'>{message.text}</p>
          </>
        )
      }
    </>
  );
}

export default Form;
