import React from 'react';
import Header from './Header.js';
import Footer from './Footer.js';
import Main from './Main.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';

function App() {
  let isEditProfilePopupOpen = false;
  console.log(isEditProfilePopupOpen);
  let isAddPlacePopupOpen = false;
  let isEditAvatarPopupOpen = false;

  function handleEditAvatarClick() {
      isEditProfilePopupOpen = true;
    // console.log(isEditProfilePopupOpen);
  }

  function handleEditProfileClick() {

  }

  function handleAddPlaceClick() {

  }

  return (
    <div className="App">
      <div className="page">
        <div className="page__container">
          <Header />
          <Main 
          onEditAvatar={() => {
            document.querySelector('.modal_type_update-avatar');
          }}
          onEditProfile={() => {
            // document.querySelector('.modal_type_edit-profile');
            handleEditAvatarClick();
            console.log(handleEditAvatarClick());
          }}
          onAddPlace={() => {
            document.querySelector('.modal_type_add-card');
          }}
          />
          <Footer />
        </div>
        <PopupWithForm 
          name={'edit-profile'} 
          title={'Редактировать профиль'} 
          children={
            <>
              <label>
                <input type="text" className="modal__item modal__item_type_name" name="name" defaultValue="Жак-Ив Кусто" placeholder="ФИО" required minLength="2" maxLength="40" />
                <span id="name-error" className="modal__item-error"></span>
              </label>
              <label>
                <input type="text" className="modal__item modal__item_type_profession" name="about" defaultValue="Исследователь океана" placeholder="Профессия" required minLength="2" maxLength="200" />
                <span id="about-error" className="modal__item-error"></span>
              </label>
            </>
          } 
          text={'Сохранить'}
          isOpen={isEditProfilePopupOpen} 
        />
        <PopupWithForm 
          name={'add-card'} 
          title={'Новое место'} 
          children={
            <>
              <label>
                <input type="text" className="modal__item modal__item_type_place" name="name" defaultValue="" placeholder="Название" required minLength="1" maxLength="30" />
                <span id="name-error" className="modal__item-error"></span>
              </label>
              <label>
                <input type="url" className="modal__item modal__item_type_link-place" name="link" defaultValue="" placeholder="Ссылка на картинку" required />
                <span id="link-error" className="modal__item-error"></span>
              </label>
            </>
          } 
          text={'Создать'}
          isOpen={isAddPlacePopupOpen}
        />
        <PopupWithForm 
          name={'confirm'} 
          title={'Вы уверены?'} 
          text={'Да'} 
        />
        <PopupWithForm 
          name={'update-avatar'} 
          title={'Обновить аватар'} 
          children={
            <>
              <label>
                <input type="url" className="modal__item modal__item_type_avatar" name="avatar" defaultValue="" placeholder="Ссылка на фото" required />
                <span id="avatar-error" className="modal__item-error"></span>
              </label>
            </>
          } 
          text={'Сохранить'}
          isOpen={isEditAvatarPopupOpen} 
        />
        <div className="modal modal_type_photo">
          <div className="modal__container-photo">
            <button className="modal__close-button modal__close-button_type_photo" type="button"></button>
            <img src="https://pictures.s3.yandex.net/frontend-developer/cards-compressed/kholmogorsky-rayon.jpg" alt="фото" className="modal__photo" />
            <p className="modal__text">Фото</p>
          </div>
        </div>
        <template id="element-template">
          <li className="element">
            <button className="element__photo" type="button">
              <img src="#" alt="фото" className="element__image" />
            </button>
            <div className="element__info-container">
              <h2 className="element__title"></h2>
              <div className="element__like-container">
                <button className="element__group" type="button"></button>
                <p className="element__counter">234</p>
              </div>
            </div>
            <button className="element__trash " type="button"></button>
          </li>
        </template>
      </div>
    </div>
  );
}

export default App;
